import styled from 'styled-components'
import { gray1, gray2, gray3, gray4 , black1, green1 } from '../../constants.js'

const SongPageConatainer = styled.div`
  width: 100%;
  min-height: calc(100vh - 80px);
  background: ${gray1};
  position: relative;
`
const MainInfoBackground = styled.div`
  width: 100%;
  height: 500px;
  border: 1px solid green;
  background-image: url('https://i.imgur.com/Ti5yQhBh.jpg');
  background-size: 180%;
  background-position: center;
  position: relative;
  display: flex;
  justify-content: center;
  padding-top: 80px;

  &:after {
    content: '';
    position: abslute;
    z-index: 1;
    width: 100%;
    height: 100%;
    backdrop-filter: blur(30px) brightness(40%);
    -webkit-backdrop-filter: blur(30px) brightness(40%);
  }
`

const MainInfoContainer = styled.div`
  position: absolute;
  width:80%;
  height:100%;
  z-index:10;
  display: flex;
  justify-content: center;
`

const BgFilter = styled.div`
  position: abslute;
  z-index: 1;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(30px) brightness(40%);
  -webkit-backdrop-filter: blur(30px) brightness(40%);
`

const MainImgDiv = styled.div`
  margin-top: 100px;
  position: relative;
  z-index: 10;
  width: 400px;
  height: 400px;
  border: 10px solid white;
  border-radius: 10px;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const MainInfoDiv = styled.div`
  position: relative;
  z-index: 10;
  margin-top: 100px;
  width: calc(100% - 400px);
  height: calc(100% - 180px);
  border: 1px solid blue;
  display: flex;
  flex-direction:column;
  padding: 20px;
`

const MainInfoSongName = styled.h1`
  font-size: 2rem;
  width:100%;
  color: white;
`

const MainInfoSongInfo = styled.div`

`

const Song = () => {
  return (
    <SongPageConatainer>
      <MainInfoBackground>
        <MainInfoContainer>
          <MainImgDiv><img src='https://p3.pstatp.com/origin/pgc-image/04e1adade5804e39a3d2ca067c00550f.jpeg'/></MainImgDiv>
          <MainInfoDiv>
            <MainInfoSongName>奇巧計程車OP1 —— 羊駝好婆</MainInfoSongName>
          </MainInfoDiv>
        </MainInfoContainer>
      </MainInfoBackground>
    </SongPageConatainer>
  )
}

export default Song
