import styled from 'styled-components'
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'
import { faPlayCircle, faHeart as faHeartBorder } from '@fortawesome/free-regular-svg-icons'
import { setCurrentSong } from "../../redux/reducers/songReducer";
import { setMode, setMainGetIn } from "../../redux/reducers/playerControlReducer"
import { gray1, gray2, gray3, gray4 } from '../../constants.js'
import { Carousel } from 'react-bootstrap';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items:center;
  width:100%;
  padding-top: 100px;
  positon: relative;
  z-index:1;
  /* border: 1px solid blue; */
`


const SongsList = styled.section`
  /* border: 1px solid pink; */
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 70%;
  padding: 0 30px;
`

const Song = styled.div`
  /* border: 1px solid yellow; */
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
  height: 120px;
  border-bottom: 1px solid ${gray2};
  padding: 0 20px;

  &:hover {
    background: white;
  }
`

const SongInfo = styled.div`
  width: 86%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  align-items: center;
`

const SongName = styled.h3`
  font-size: 1.2rem;
  font-weight: bold;
`

const AuthorName = styled.p`
  font-size: 0.8rem;
  color: ${gray3}
`

const SongButtons = styled.div`
  width: 14%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  justify-content: end;
  align-items: center;
`

const NumberDiv = styled.div`
  width: 60px;
  /* border: 1px solid blue; */
`

const ImgDiv = styled.div`
  height: 80px;
  width: 80px;
  /* border: 1px solid black; */
  border-radius: 12px;
  overflow: hidden;
  position: relative;
  cursor:pointer;
  z-index:1;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    position: relative;
    z-index: 2;
  }
`


const SongDesc = styled.div`
  display:flex;
  flex-direction: column;
  justify-content: center;
  margin-left: 20px;
`

const Music = () => {
  const dispatch = useDispatch()
  const songs = useSelector((store) => store.song.songs)
  const playSong = async (song) => {
    await dispatch(setCurrentSong(song))
    await dispatch(setMode(2))
    await dispatch(setMainGetIn(true))
    await dispatch(setMainGetIn(false))
  }
  return (
    <Container >
    <Carousel>
<Carousel.Item interval={1000}>
  <img
    className="d-block w-100"
    src="holder.js/800x400?text=First slide&bg=373940"
    alt="First slide"
  />
  <Carousel.Caption>
    <h3>First slide label</h3>
    <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
  </Carousel.Caption>
</Carousel.Item>
<Carousel.Item interval={500}>
  <img
    className="d-block w-100"
    src="holder.js/800x400?text=Second slide&bg=282c34"
    alt="Second slide"
  />
  <Carousel.Caption>
    <h3>Second slide label</h3>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
  </Carousel.Caption>
</Carousel.Item>
<Carousel.Item>
  <img
    className="d-block w-100"
    src="holder.js/800x400?text=Third slide&bg=20232a"
    alt="Third slide"
  />
  <Carousel.Caption>
    <h3>Third slide label</h3>
    <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
  </Carousel.Caption>
</Carousel.Item>
</Carousel>
      <SongsList>
        {songs.map((song,index) => {
          return (
            <Song>
              <SongInfo>
                <NumberDiv>
                  <h1>{index + 1}</h1>
                </NumberDiv>
                <ImgDiv onClick={() => playSong(song)}>
                  <img src={song.cover}/>
                </ImgDiv>
                <SongDesc>
                  <SongName>{song?.name}</SongName>
                  <AuthorName>{song?.artist}</AuthorName>
                </SongDesc>
              </SongInfo>
              <SongButtons>
                <FontAwesomeIcon size='1x' icon={faHeartBorder}/>
              </SongButtons>
            </Song>
          )
        })}
      </SongsList>
    </Container>
  )
}

export default Music
