import React, { useState, useEffect } from 'react'
import { FaBars } from 'react-icons/fa'
import { Nav, NavbarContainer, NavLogo, MobileIcon, NavMenu, NavItem, NavLinks, NavBtn, NavBtnLink, NavMemberImgDiv} from './HeaderElements'
import firebase from '../../utils/firebase.js'
import 'firebase/compat/auth';
const Header = ({ toggleSignPanel, toggleSideBar }) => {
  const [user, setUser] = useState(null)
  useEffect(() => {
    firebase
      .auth()
      .onAuthStateChanged((currentUser) => {
        setUser(currentUser)
      })
  },[])
  const signOut = () => {
    firebase.auth().signOut()
  }
  return (
    <Nav>
      <NavbarContainer>
        <NavLogo to="/">Indielog</NavLogo>
        <MobileIcon onClick={toggleSideBar}>
          <FaBars />
        </MobileIcon>
        <NavMenu>
          <NavItem>
            <NavLinks to="/">聽聽音樂</NavLinks>
          </NavItem>
          <div style={{color: '#fff'}}> | </div>
          <NavItem>
            <NavLinks to="/blog">聊聊音樂</NavLinks>
          </NavItem>
        </NavMenu>
        <NavBtn>
        {user ? <NavMemberImgDiv><img src={'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdPqoKCJL-SvFNW_wa72-6vZ8x3jP0k6EPZw&usqp=CAU'}/></NavMemberImgDiv> : <NavBtnLink onClick={toggleSignPanel}>註冊 / 登入</NavBtnLink>}
        </NavBtn>
      </NavbarContainer>
    </Nav>
  )
}

export default Header
