import { useState, useEffect } from 'react'
import styled from 'styled-components'
import { faPen } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import MemberTitileButtonsGroup from '../../components/MemberTitileButtonsGroup/index.js'
import { gray1, gray2, gray3, gray4, green1, green2, black1, MEDIA_QUERY_568, MEDIA_QUERY_768 } from '../../constants.js'


const PersonalInfoContainer = styled.div`
  width: 94%;
  background: white;
  margin: 30px 0;
  border-radius: 10px;
  box-shadow: 0px 0px 6px rgba(33, 33, 33, 0.2);
  padding: 16px 18px;
`

const InfoSection = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 30px;
`

const InfoTitle = styled.h3`
  position: relative;
  &:before {
    position: absolute;
    content:'';
    background: ${green1};
    width: 112px;
    height: 4px;
    border: none;
    bottom: -6px;

  }
`

const InfoElementTitle = styled.h4`
  color: ${gray4};
  font-weight: normal;
`
const InfoElementMainDiv = styled.div`
  width: 100%;
  display: flex;
  align-items: flex-end;
`
const ImgDiv = styled.div`
  width:  200px;
  height: 200px;
  border-radius: 20px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const EditButton = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  font-size: 0.8rem;
  color: white;
  background: ${green1};
  height: 30px;
  width: 30px;
  border: none;
  padding: 6px 10px;
  margin: 0 20px;
  transition: all 0.3s ease;
  cursor: pointer;
  &:hover {
    background: ${green2}
  }
`

const Input = styled.input`
  width: 300px;
  padding: 4px 10px;
  height: 40px;
  border: 1px solid ${gray2};
  border-radius: 12px;
  outline: none;
  color: ${gray3};
`

const IntroductionArea = styled.div`
  width:100%;
  min-height: 200px;
  font-size: 1rem;
  color: ${gray3};
  padding: 10px;
  margin-bottom: 12px;
  word-wrap: break-word;
  border: 1px solid ${gray2};
  border-radius: 12px;
`

const IntroductionDiv = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const ModalBackground = styled.div`
  position: fixed;
  top:0;
  left:0;
  width: 100%;
  height: 100vh;
  background: rgba(33, 33, 33, 0.8);
  z-index: 1010;
`

const ModalPanel = styled.div`
  position: absolute;
  width: 80%;
  background: white;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 360px;
  height: 500px;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 10px;
  justify-content: space-between;
  padding: 20px 10px;
`

const ModalInnerTop = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 80%;
`

const ModalInnerDiv = styled.div`
  margin-top: 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width:100%;
`

const ModalInnerBottom = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 20%;
`

const ModalButton = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 6px;
  font-size: 1rem;
  color: white;
  background: ${green1};
  height: 30px;
  width: 80px;
  border: none;
  padding: 6px 10px;
  margin: 0 10px;
  transition: all 0.3s ease;

  cursor: pointer;
  &:hover {
    background: ${green2}
  }
`
const orders = ['會員資料','說讚的音樂','說讚的文章']

const PersonalInfo = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false)
  return (
      <>
        <MemberTitileButtonsGroup order={order}/>
        <PersonalInfoContainer>
          <InfoTitle>基本資料</InfoTitle>
          <InfoSection>
            <InfoElementTitle>頭像</InfoElementTitle>
            <InfoElementMainDiv>
              <ImgDiv><img src={'https://cdn.hk01.com/di/media/images/cis/5e4270c8a5e2c82bd6096139.jpg/KNyBGtInTJ6vNZG50MWr4YRe57jWFOUilG8xy5RvMcs?v=w1920'}/></ImgDiv>
              <EditButton onClick={() => setModalIsOpen(!modalIsOpen)}><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>帳號</InfoElementTitle>
            <InfoElementMainDiv>
              <Input disabled value={'oxox@gmail.com'}/>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>密碼</InfoElementTitle>
            <InfoElementMainDiv>
              <Input disabled value={'******'}/>
              <EditButton onClick={() => setModalIsOpen(!modalIsOpen)}><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>暱稱</InfoElementTitle>
            <InfoElementMainDiv>
              <Input disabled value={'香菇肉燥飯'}/>
              <EditButton onClick={() => setModalIsOpen(!modalIsOpen)}><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>自我介紹</InfoElementTitle>
            <IntroductionDiv>
              <IntroductionArea>先帝創業未半，而中道崩殂；今天下三分，益州疲弊，此誠危急存亡之秋也！然侍衞之臣，不懈於內；忠志之士，忘身於外者，蓋追先帝之殊遇，欲報之於陛下也。</IntroductionArea>
              <EditButton onClick={() => setModalIsOpen(!modalIsOpen)}><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </IntroductionDiv>
          </InfoSection>
          {modalIsOpen &&
            <ModalBackground>
              <ModalPanel>
                <InfoTitle>修改密碼</InfoTitle>
                <ModalInnerTop>
                  <ModalInnerDiv>
                    <InfoElementTitle>輸入新密碼</InfoElementTitle>
                    <Input />
                  </ModalInnerDiv>
                  <ModalInnerDiv>
                    <InfoElementTitle>再次輸入新密碼</InfoElementTitle>
                    <Input />
                  </ModalInnerDiv>
                </ModalInnerTop>
                <ModalInnerBottom>
                  <ModalButton>送出</ModalButton>
                  <ModalButton onClick={() => setModalIsOpen(!modalIsOpen)}>取消</ModalButton>
                </ModalInnerBottom>
              </ModalPanel>
