import { useState } from 'react'
import SignUp from './SignUp.js'
import SignIn from './SignIn.js'
import {
  SignPanelContainer,
  SignPanelLogo,
  Panel,
  CancelButton,
  PanelInnerWrap
} from './SignPanelElements.js'
const SignPanel = ({ toggleSignPanel }) => {
  const [panelStatus, setPanelStatus] = useState('signup')
  return (
      <SignPanelContainer>
        <Panel>
          <CancelButton onClick={toggleSignPanel} />
          <SignPanelLogo>Indielog</SignPanelLogo>
          <PanelInnerWrap>
          { panelStatus && <SignUp setPanelStatus={setPanelStatus}/>}

          </PanelInnerWrap>
        </Panel>
      </SignPanelContainer>
  )
}

export default SignPanel
