  width: 100%;
  display: grid;
  grid-template-columns: 1fr 3fr;
`

const PostsContainer = styled.div`
  width: 80%;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
`

const TopicsContainer = styled.div`
  width: 20%;
  height:400px;
  border-radius: 6px;
  border: 1px solid grey;
  margin-top: 40px;
`

const Blog = () => {
  const [topics, setTopics] = useState([])
  return (
    <BlogPageContainer>
      <TopicsContainer/>
      <PostsContainer>
        <Post/>
        <Post/>
        <Post/>
        <Post/>
      </PostsContainer>
    </BlogPageContainer>
  )
}

export default Blog
