import { useState, useEffect } from 'react'
import {
  CommentsContainer,
  CommentAddSection,
  Notice,
  CommentAddArea,
  CommentInfo,
  CommentorImgDiv,
  CommentorName,
  CommentorDate,
  CommentTextArea,
  CommentButtons,
  CommentContent,
} from './CommentAreaElements.js'
import Comment from '../Comment'
import firebase from '../../utils/firebase.js'
import 'firebase/compat/auth';
import "firebase/compat/firestore"

const CommentArea = ({ postId }) => {
  const user = firebase.auth().currentUser
  const [commentInput, setCommentInput] = useState('')
  const [comments, setComments] = useState([])
  const [commentsWithAutorInfo, setCommentsWithAutorInfo] = useState([])
  const handleCommentInputSubmit = () => {
    firebase
      .firestore()
      .collection('posts')
      .doc(postId)
      .collection('comments')
      .doc()
      .set({
        content: commentInput,
        createdAt: firebase.firestore.Timestamp.now(),
        authorUid: user.uid,
      }).then(() => {
        setCommentInput('')
      })
  }
  useEffect(() => {
    firebase
      .firestore()
      .collection('posts')
      .doc(postId)
      .collection('comments')
      .onSnapshot((collectionSnapShot) => {
         const data = collectionSnapShot.docs.map((doc) => {
           return {
             authorUid: doc.data().authorUid,
             content: doc.data().content,
             createdAt: doc.data().createdAt
           }
         })
         setComments(data)
      })
  },[])
  return (
    <CommentsContainer>
      <CommentAddSection>
        <h3>留言</h3>
        {user ?
          <CommentAddArea>
            <CommentInfo>
              <CommentorImgDiv><img src={user.photoURL ? user.photoURL : 'https://assets.juksy.com/files/articles/108444/800x_100_w-60934d08db2e1.jpg'}/></CommentorImgDiv>
              <CommentorName>{user.displayName}</CommentorName>
            </CommentInfo>
            <CommentTextArea placeholder='點此輸入留言...' value={commentInput} onChange={(e)=> setCommentInput(e.target.value)}>
            </CommentTextArea>
            <CommentButtons>
              <p onClick={handleCommentInputSubmit}>送出留言</p>
            </CommentButtons>
          </CommentAddArea>
          :
          <Notice>請先<span>登入</span>以留言</Notice>
        }
      </CommentAddSection>
      {comments.map(comment => {
        return (
          <Comment>
            <CommentInfo>
              <CommentorImgDiv><img src={comment.author?.photoURL ? comment.author.photoURL : 'https://assets.juksy.com/files/articles/108444/800x_100_w-60934d08db2e1.jpg'}/></CommentorImgDiv>
              <CommentorName>{comment.author?.displayName}</CommentorName>
              <CommentorDate>{comment.createdAt?.toDate().toLocaleString()}</CommentorDate>
            </CommentInfo>
            <CommentContent>
              {comment.content}
            </CommentContent>
            <CommentButtons>
              <p>（1）</p>
              <p>（2）</p>
              <p>（3）</p>
            </CommentButtons>
          </Comment>
        )
      })}

    </CommentsContainer>
  )
}

export default CommentArea
