import { useState, useEffect } from 'react'
import styled from 'styled-components'
import { faPen } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { gray1, gray2, gray3, gray4, green1, green2, black1, MEDIA_QUERY_568, MEDIA_QUERY_768 } from '../../constants.js'

const MemberPageContainer = styled.div`
  width: 80%;
  margin: 0 auto 20px;
  max-width: 860px;
  min-height: calc(100vh - 80px);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 100px;

  ${MEDIA_QUERY_568} {
    width: 100%;
  }
`

const TitleButtonsGroup = styled.div`
  display: flex;
  width: 100%;
  padding: 10px 0 20px;
  border-bottom: 1px solid ${gray2};
`

const TitleNormalButton = styled.button`
  font-size: 0.9rem;
  color: ${gray3};
  background: none;
  border: none;
  margin: 10px 20px;
  transition: all 0.3s ease;
  &:hover {
    color: ${black1};
    transform: translateY(-10%);
  }
`

const TitleActiveButton = styled.button`
  border-radius: 30px;
  color: white;
  background: ${green1};
  width: 100px;
  border: none;
  padding: 6px 10px;
  margin: 10px 20px;
`

const PersonalInfoContainer = styled.div`
  width: 94%;
  background: white;
  margin: 30px 0;
  border-radius: 10px;
  box-shadow: 0px 0px 6px rgba(33, 33, 33, 0.2);
  padding: 16px 18px;
`

const InfoSection = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 30px;
`

const InfoTitle = styled.h3`
`

const InfoElementTitle = styled.h4`
  color: ${gray4};
  font-weight: normal;
`
const InfoElementMainDiv = styled.div`
  width: 100%;
  display: flex;
  align-items: flex-end;
`
const ImgDiv = styled.div`
  width:  200px;
  height: 200px;
  border-radius: 20px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const EditButton = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  font-size: 0.8rem;
  color: white;
  background: ${green1};
  height: 30px;
  width: 30px;
  border: none;
  padding: 6px 10px;
  margin: 0 20px;
  transition: all 0.3s ease;
  cursor: pointer;
  &:hover {
    background: ${green2}
  }
`

const Input = styled.input`
  width: 300px;
  padding: 4px 10px;
  height: 40px;
  border: 1px solid ${gray2};
  border-radius: 12px;
  outline: none;
  color: ${gray3};
`

const IntroductionArea = styled.div`
  width:100%;
  min-height: 200px;
  font-size: 1rem;
  color: ${gray2};
  padding: 10px;
  margin-bottom: 12px;
  word-wrap: break-word;
  border: 1px solid ${gray2};
`

const IntroductionDiv = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const PersonalInfo = () => {
  return (
      <MemberPageContainer>
        <TitleButtonsGroup>
          <TitleNormalButton>說讚的音樂</TitleNormalButton>
          <TitleNormalButton>說讚的文章</TitleNormalButton>
          <TitleActiveButton>會員資料</TitleActiveButton>
        </TitleButtonsGroup>
        <PersonalInfoContainer>
          <InfoTitle>基本資料</InfoTitle>
          <InfoSection>
            <InfoElementTitle>頭像</InfoElementTitle>
            <InfoElementMainDiv>
              <ImgDiv><img src={'https://cdn.hk01.com/di/media/images/cis/5e4270c8a5e2c82bd6096139.jpg/KNyBGtInTJ6vNZG50MWr4YRe57jWFOUilG8xy5RvMcs?v=w1920'}/></ImgDiv>
              <EditButton><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>帳號</InfoElementTitle>
            <InfoElementMainDiv>
              <Input />
              <EditButton><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>密碼</InfoElementTitle>
            <InfoElementMainDiv>
              <Input />
              <EditButton><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>暱稱</InfoElementTitle>
            <InfoElementMainDiv>
              <Input />
              <EditButton><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </InfoElementMainDiv>
          </InfoSection>
          <InfoSection>
            <InfoElementTitle>自我介紹</InfoElementTitle>
            <IntroductionDiv>
              <IntroductionArea />
              <EditButton><FontAwesomeIcon size='1x' icon={faPen}/></EditButton>
            </IntroductionDiv>
          </InfoSection>
        </PersonalInfoContainer>
      </MemberPageContainer>
  )
}

export default PersonalInfo
