import styled from 'styled-components'
import { useSelector } from "react-redux";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
  faHeart,
} from '@fortawesome/free-solid-svg-icons'
import { gray1, gray2, gray3, gray4 } from '../constants.js'
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items:center;
  width:100%;
  padding-top: 100px;
  /* border: 1px solid blue; */
`
const CarouSelll = styled.div`
`

const SongsList = styled.section`
  /* border: 1px solid pink; */
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 70%;
  padding: 0 30px;
`

const Song = styled.div`
  /* border: 1px solid yellow; */
  display: flex;
  align-items: center;
  width: 100%;
  height: 120px;
`

const SongInfo = styled.div`
  width: 86%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  align-items: center;
`

const SongButtons = styled.div`
  width: 14%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  justify-content: end;
  align-items: center;
`

const NumberDiv = styled.div`
  width: 60px;
  /* border: 1px solid blue; */
`

const ImgDiv = styled.div`
  height: 80px;
  width: 80px;
  /* border: 1px solid black; */
  border-radius: 12px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const SongDesc = styled.div`
  display:flex;
  flex-direction: column;
  justify-content: center;
  margin-left: 10px;
`

const Music = () => {
  const songs = useSelector((store) => store.song.songs)
  return (
    <Container >
      <CarouSelll>
      </CarouSelll>
      <SongsList>
        {songs.map(song => {
          return (
            <Song>
              <SongInfo>
                <NumberDiv>
                  <h1>1</h1>
                </NumberDiv>
                <ImgDiv>
                  <img src={song.cover}/>
                </ImgDiv>
                <SongDesc>
                  <h3>{song?.name}</h3>
                  <h4>{song?.artist}</h4>
                </SongDesc>
              </SongInfo>
              <SongButtons>
                <FontAwesomeIcon size='1x' icon={faHeart}/>
              </SongButtons>
            </Song>
          )
        })}

      </SongsList>
    </Container>
  )
}

export default Music
