import { useState, useEffect } from 'react'
import styled from 'styled-components'
import firebase from '../../utils/firebase'
import "firebase/compat/firestore"

const BlogPostPageContainer = styled.div`
  border: 1px solid green;
  width: 80%;
  margin: 60px auto 20px;
  max-width: 860px;
  min-height: 800px;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Title = styled.h2`
  align-self: flex-start;
`
const Form = styled.form`

`

const Input = styled.input`

`

const TextArea = styled.textarea`

`

const Select = styled.select`

`

const Image = styled.img`

`

const Button = styled.label`

`

const NewPost = () => {
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [topic, setTopic] = useState('')
  const [topics, setTopics] = useState([])
  useEffect(() => {
    firebase
      .firestore()
      .collection('topics')
      .get()
      .then((collectionSnapShot) => {
        const data = collectionSnapShot.docs.map((doc) => {
          return doc.data()
        })
        setTopics(data)
      })
  },[])

  return (

    <BlogPostPageContainer>
      <Title>發表文章</Title>
      <Form>
        <Image  src=''/>
        <Button htmlFor='post-image' >上傳文章圖片</Button>
        <Input type="file" id='post-image' style={{display: 'none'}}/>
        <Input placeholder='請輸入文章標題'/>
        <TextArea placeholder='請輸入文章內容'/>
        <Select>
          {topics.map((topic) => {
            return <option value={topic.name}>{topic.name}</option>
          })}

        </Select>
      </Form>
    </BlogPostPageContainer>
  )
}

export default NewPost
