import { useState, useEffect } from 'react'
import styled from 'styled-components'
import { gray1, gray2, gray3, gray4, green1, green2, black1, MEDIA_QUERY_568, MEDIA_QUERY_768 } from '../../constants.js'

const MemberPageContainer = styled.div`
  width: 80%;
  margin: 0 auto 20px;
  max-width: 860px;
  min-height: calc(100vh - 80px);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 100px;
  border: 1px solid blue;
`

const TitleButtonsGroup = styled.div`
  display: flex;
  width: 100%;
  padding: 10px 0 20px;
  border-bottom: 1px solid ${gray2};
`

const TitleNormalButton = styled.button`
  font-size: 0.9rem;
  color: ${gray3};
  background: none;
  border: none;
  margin: 10px 20px;
  transition: all 0.3s ease;
  &:hover {
    color: ${black1};
    transform: translateY(-10%);
  }
`

const TitleActiveButton = styled.button`
  border-radius: 30px;
  color: white;
  background: ${green1};
  width: 100px;
  border: none;
  padding: 6px 10px;
  margin: 10px 20px;
`

const PersonalInfoContainer = styled.div`
  width: 94%;
  height: 500px;
  background: white;
  margin: 30px 0;
  border-radius: 10px;
  box-shadow: 0px 0px 6px rgba(33, 33, 33, 0.2);
`

const InfoSection = styled.div`
  display: flex;
  flex-direction: column;
`

const InfoTitle = styled.h2`

`

const PersonalInfo = () => {
  return (
      <MemberPageContainer>
        <TitleButtonsGroup>
          <TitleNormalButton>說讚的音樂</TitleNormalButton>
          <TitleNormalButton>說讚的文章</TitleNormalButton>
          <TitleActiveButton>會員資料</TitleActiveButton>
        </TitleButtonsGroup>
        <PersonalInfoContainer>
          <InfoSection>
            <InfoTitle>基本資料</InfoTitle>
          </InfoSection>
        </PersonalInfoContainer>
      </MemberPageContainer>
  )
}

export default PersonalInfo
