import { useState, useEffect } from 'react'
import styled from 'styled-components'
import firebase from '../../utils/firebase'
import "firebase/compat/firestore"
import { useParams } from 'react-router-dom'
import { gray1, gray2, gray3, gray4, black1, MEDIA_QUERY_768 } from '../../constants.js'

const BlogPostPageContainer = styled.div`
  border: 1px solid green;
  width: 80%;
  margin: 0 auto 20px;
  max-width: 860px;
  min-height: 800px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 140px;
  background: ${gray1}
`

const PostContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: ${gray1};
`

const PostPic = styled.img`
  width: 120%;
  margin-bottom: 20px;
`

const PostDesc = styled.p`
  font-size: 0.7rem;
  color: #bbb
`

const PostTitle = styled.h1`
  font-weight: bold;
  font-size: 2rem;
  margin: 0 80px 40px;
`

const PostContentWrapper = styled.div`
  width:100%;
  margin-top: 40px;
  color: #666;
  word-wrap: break-word;
  p {
    margin: 20px 0;
  }

  img {
    margin: 20px 0;
    height: auto;
    max-width: 100%;
  }

`

const AuthorContainer = styled.div`
  margin: 60px 0;
  border: 1px solid ${gray3};
  border-radius: 30px 6px 30px 6px;
  width: 86%;
  height: 200px;
  display: flex;
  padding: 10px 28px;
  justify-content: space-between;
  align-items: center;

  ${MEDIA_QUERY_768} {
    padding: 10px 10px;
  }

`

const AuthorImg = styled.div`
  width:  130px;
  height: 130px;
  border-radius: 50%;
  overflow: hidden;

  ${MEDIA_QUERY_768} {
    width: 90px;
    height: 90px;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const AuthorInfo = styled.div`
  height: 100%;
  width: 72%;
  display: flex;
  flex-direction: column;
  padding-top: 20px;
  word-wrap: break-word;

  ${MEDIA_QUERY_768} {
    padding-top: 40px;
  }
`

const AuthorName = styled.h3`
  font-size: 1.5rem;

  ${MEDIA_QUERY_768} {
    font-size: 1.3rem;
  }
`

const AuthorDesc = styled.h4`
  font-size: 1rem;
  color: ${gray3};

  ${MEDIA_QUERY_768} {
    font-size: 0.8rem;
  }
`

const CommentsContainer = styled.div`
  border-top: 1px solid ${gray2};
  width: 100%;
`

const Comment = styled.div`
  margin: 30px 0 ;
  min-height: 180px;
  width: 100%;
  background: white;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
`

const CommentInfo = styled.div`
  display: flex;
  height: 66px;
  align-items: center;
  padding-left: 20px;
`

const CommentorImgDiv = styled.div`
width:  40px;
height: 40px;
border-radius: 50%;
overflow: hidden;

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
`

const CommentorName = styled.h4`
  font-size: 1.3rem;
  margin-left: 6px;
`

const CommentorDate = styled.p`
  display: flexbox;
  font-size: 0.8rem;
  line-height: 0.8rem;
  margin-left: 6px;
  padding-top: 14px;
  color: ${gray3}
`

const CommentMain =  styled.div`
  width: 100%;
  min-height: 114%;
  display: flex;
  flex-direction: column;
  justify-cotent: space-between;
`

const CommentContent = styled.div`
  height: 100%;
  border: 1px solid orange;
  width: 100%;
  padding: 0 10px;
  color: ${gray4};
  word-wrap: break-word;
`

const CommentButtons = styled.div`
  border: 1px solid green;
  height: 36px;
  width: 100%;
  display: flex;
  justify-content: center;
  aling-items: center;
`



const BlogPost = () => {
  const { postId } = useParams()
  const[post, setPost] = useState({})
  useEffect(() => {
    firebase
      .firestore()
      .collection('posts')
      .doc(postId)
      .get()
      .then((docSnapShot) => {
        const data = docSnapShot.data()
        setPost(data)
      })
  },[])
  return (
    <>
    <BlogPostPageContainer>
      <PostContainer>
      <PostTitle>{post.title}</PostTitle>
      <PostPic src={post.imageUrl}></PostPic>
      <PostDesc>{post.createdAt?.toDate().toLocaleString()} | {post.topic}</PostDesc>
      <PostContentWrapper>{post.content}</PostContentWrapper>
      </PostContainer>
      <AuthorContainer>
        <AuthorImg><img src={'https://assets.juksy.com/files/articles/108444/800x_100_w-60934d08db2e1.jpg'}/></AuthorImg>
        <AuthorInfo>
          <AuthorName>海象</AuthorName>
          <AuthorDesc>只是一個平凡的計程車司機asdadasdasdasdasdasdasdasafldafljfaldldmsaldmslamdl
          </AuthorDesc>
        </AuthorInfo>
      </AuthorContainer>
      <CommentsContainer>
        <h3>留言</h3>
        <Comment>
          <CommentInfo>
            <CommentorImgDiv><img src={'https://assets.juksy.com/files/articles/108444/800x_100_w-60934d08db2e1.jpg'}/></CommentorImgDiv>
            <CommentorName>BB88</CommentorName>
            <CommentorDate>2021-05-03 12:00:00</CommentorDate>
          </CommentInfo>
          <CommentMain>
            <CommentContent>
              mdl 是一個平凡的計程車司機asdadasdasdasdasdasdasdasafldafljfaldldmsaldmslamdl
            </CommentContent>
          </CommentMain>
          <CommentButtons>
            <p>（1）</p>
            <p>（2）</p>
            <p>（3）</p>
          </CommentButtons>
        </Comment>
        <Comment/>
        <Comment/>
        <Comment/>
      </CommentsContainer>
    </BlogPostPageContainer>
    </>
  )
}

export default BlogPost
