import { useState, useEffect, useRef } from 'react'
import Post from '../../components/Post'
import { getTopics, getPostsAmount, getSeedPosts, getChangePagePosts } from '../../utils/firebase'
import { useLocation } from 'react-router-dom'
import {
  BlogPageContainer,
  PostsContainer,
  TopicsContainer,
  Title,
  TopicsWrap,
  Topic,
  PostLink
} from './BlogElements.js'


import styled from 'styled-components'

const PaginatorContainer = styled.div`
  margin-top: 30px;
  width: 500px;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
`

const PageBtn = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 30px;
  width: 30px;
  margin: 0 10px;
  border-radius: 50%;
  border: 1px solid black;
  font-size: 12px;
`

const PageBtnBack = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 30px;
  width: 30px;
  margin: 0 10px;
  border-radius: 50%;
  border: 1px solid black;
  font-size: 12px;
  margin-right: auto;
`

const PageBtnNext = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 30px;
  width: 30px;
  margin: 0 10px;
  border-radius: 50%;
  border: 1px solid black;
  font-size: 12px;
  margin-left: auto;
`

const Blog = () => {
  const [topics, setTopics] = useState([])
  const [posts, setPosts] = useState([])
  const [totalPages, setTotalPages] = useState(0)
  const [currentPage, setCurrentPage] = useState(1)
  const lastPostSnapshotRef = useRef()
  const location = useLocation()
  const urlSearchParams = new URLSearchParams(location.search)
  const currentTopic = urlSearchParams.get('topic')
  useEffect(() => {
    getTopics(setTopics)
  },[])
  useEffect(() => {
    getPostsAmount(currentTopic, 5, setTotalPages)
  },[currentTopic])
  useEffect(() => {
    getSeedPosts(currentTopic, 5, setPosts)
  },[currentTopic])

  function nextPage(){
    if(currentPage === totalPages) return
    const lastRefPlace = 5 * (currentPage)
    getChangePagePosts(currentTopic, 5, setPosts, lastRefPlace)
    setCurrentPage((currentPage+1))
  }
  function prevPage(){
    if(currentPage === 1) return
    const lastRefPlace = 5 * (currentPage-2)
    getChangePagePosts(currentTopic, 5, setPosts, lastRefPlace)
    setCurrentPage((currentPage-1))
  }

  useEffect(() => {
    console.log(totalPages)
  },[totalPages])
  useEffect(() => {
    console.log(currentPage)
  },[currentPage])
  return (
    <BlogPageContainer>
      <TopicsContainer>
        <Title>分類</Title>
        <TopicsWrap>
        <Topic
         to='/blog'
         active={!currentTopic}
        >全部文章</Topic>
        {topics.map(topic => {
          return <Topic
                    key={topic.id}
                    to={`/blog?topic=${topic.name}`}
                    active={currentTopic === topic.name}
                  >
                    {topic.name}
                  </Topic>
        })}
        </TopicsWrap>
      </TopicsContainer>
      <PostsContainer>
        {posts.map((post) => {
          return <PostLink key={post.id} to={`/blogpost/${post.id}`}>
                    <Post key={post.id}
                          createdAt={post.createdAt.toDate().toLocaleString([],{year: 'numeric', month: 'numeric', day: 'numeric'})}
                          imageUrl={post.imageUrl} topic={post.topic}
                          title={post.title}
                    />
                </PostLink>
        })}
      <PaginatorContainer>
        <PageBtn onClick={prevPage}>1</PageBtn>
        <PageBtn onClick={nextPage}>2</PageBtn>
      </PaginatorContainer>
      </PostsContainer>
    </BlogPageContainer>
  )
}

export default Blog
