  width: 300px;
`

const PostTitle = styled.h1`
  font-weight: bold;
  font-size: 2rem;
  margin: 0 20px 40px;
`

const PostContent = styled.div`
`

const BlogPost = () => {
  return (
    <>
      <PostContainer>

      <PostPic src={'https://blow.streetvoice.com/wp-content/uploads/2021/08/OZI-side-1568x747.jpg'}></PostPic>
      <PostContent></PostContent>
      </PostContainer>
    </>
  )
}

