import styled from 'styled-components'
import { useSelector } from "react-redux";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
  faHeart,
} from '@fortawesome/free-solid-svg-icons'
import { faPlayCircle } from '@fortawesome/free-regular-svg-icons'
import { gray1, gray2, gray3, gray4 } from '../../constants.js'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items:center;
  width:100%;
  padding-top: 100px;
  /* border: 1px solid blue; */
`
const CarouSelll = styled.div`
`

const SongsList = styled.section`
  /* border: 1px solid pink; */
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 70%;
  padding: 0 30px;
`

const Song = styled.div`
  /* border: 1px solid yellow; */
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
  height: 120px;
  border-bottom: 1px solid ${gray2};
  padding: 0 20px;

  &:hover {
    background: white;
  }

`

const SongInfo = styled.div`
  width: 86%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  align-items: center;
`

const SongName = styled.h3`
  font-size: 1.2rem;
  font-weight: bold;
`

const AuthorName = styled.p`
  font-size: 0.8rem;
  color: ${gray3}
`

const SongButtons = styled.div`
  width: 14%;
  height: 100%;
  /* border: 1px solid orange; */
  display: flex;
  justify-content: end;
  align-items: center;
`

const NumberDiv = styled.div`
  width: 60px;
  /* border: 1px solid blue; */
`

const ImgDiv = styled.div`
  height: 80px;
  width: 80px;
  /* border: 1px solid black; */
  border-radius: 12px;
  overflow: hidden;
  position: relative;
  cursor:pointer;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  &::hover::before {
    postion:absolute;
    z-index: 10;
    content:'';
    background-image: url('https://www.svgrepo.com/show/14574/play-button.svg');
    color: white;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: black;
  }
`

const SongDesc = styled.div`
  display:flex;
  flex-direction: column;
  justify-content: center;
  margin-left: 20px;
`

const Music = () => {
  const songs = useSelector((store) => store.song.songs)
  return (
    <Container >
      <CarouSelll>
      </CarouSelll>
      <SongsList>
        {songs.map((song,index) => {
          return (
            <Song>
              <SongInfo>
                <NumberDiv>
                  <h1>{index + 1}</h1>
                </NumberDiv>
                <ImgDiv>
                  <img src={song.cover}/>
                </ImgDiv>
                <SongDesc>
                  <SongName>{song?.name}</SongName>
                  <AuthorName>{song?.artist}</AuthorName>
                </SongDesc>
              </SongInfo>
              <SongButtons>
                <FontAwesomeIcon size='1x' icon={faHeart}/>
              </SongButtons>
            </Song>
          )
        })}

      </SongsList>
    </Container>
  )
}

export default Music
