import styled from 'styled-components'
import { gray1, gray2, gray3, gray4 , black1, green1 } from '../../constants.js'

const SongPageConatainer = styled.div`
  width: 100%;
  min-height: calc(100vh - 80px);
  background: ${gray1};
  position: relative;
`
const MainInfoBackground = styled.div`
  width: 100%;
  height: 500px;
  border: 1px solid green;
  background-image: url('https://i.imgur.com/Ti5yQhBh.jpg');
  overflow: hidden;
  background-size: 180%;
  background-position: center;
  position: relative;
  display: flex;
  justify-content: center;
  padding-top: 80px;

  &:after {
    content: '';
    position: abslute;
    z-index: 1;
    width: 100%;
    height: 100%;
    backdrop-filter: blur(30px) brightness(40%);
    -webkit-backdrop-filter: blur(30px) brightness(40%);
  }
`

const MainInfoContainer = styled.div`
  position: absolute;
  width:80%;
  height:100%;
  z-index:10;
  display: flex;
  justify-content: center;
`

const BgFilter = styled.div`
  position: abslute;
  z-index: 1;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(30px) brightness(40%);
  -webkit-backdrop-filter: blur(30px) brightness(40%);
`

const MainImgDiv = styled.div`
  margin-top: 100px;
  position: relative;
  z-index: 10;
  width: 400px;
  height: 400px;
  border: 10px solid black;
  border-radius: 10px;
`

const MainInfoDiv = styled.div`
  position: relative;
  z-index: 10;
  width: calc(100% - 400px);
  height: 80%;
  border: 1px solid blue;
`

const Song = () => {
  return (
    <SongPageConatainer>
      <MainInfoBackground>
        <MainInfoContainer>
          <MainImgDiv/>
          <MainInfoDiv/>
        </MainInfoContainer>
      </MainInfoBackground>
    </SongPageConatainer>
  )
}

export default Song
